import { Modal, Stack, Form, Button } from "react-bootstrap";
import { act } from "react-dom/cjs/react-dom-test-utils.production.min";
//import editarProducto from "../functions/editarProducto";

function ModalEditar({
  isModalEditar,
  setIsModalEditar,
  actualizarEstadoOrdenes,
  ordenesEditar,
}) {
  function editarOrdenesModal() {
    //obtener info del formulaio

    const idCompra = document.getElementById("idCompra").value;
    const cliente = document.getElementById("cliente").value;
    const fechaCompra = document.getElementById("fechaCompra").value;
    const fechaEntrega = document.getElementById("fechaEntrega").value;
    const courier = document.getElementById("courier").value;
    const tracking = document.getElementById("tracking").value;
    const costoCompra = document.getElementById("costoCompra").value;
    const detalle = document.getElementById("detalle").value;
    const observacion = document.getElementById("observacion").value;
    const estado = document.getElementById("estado").value;

    const infoOrden = {
      idCompra,
      cliente,
      fechaCompra,
      fechaEntrega,
      courier,
      tracking,
      costoCompra,
      detalle,
      observacion,
      estado,
    };

    actualizarEstadoOrdenes();
    setIsModalEditar(false);
  }

  return (
    <Modal show={isModalEditar} onHide={() => setIsModalEditar(false)}>
      <Modal.Header>
        <Modal.Title>Editar orden</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Stack>
            <Form.Control id="idCompra" type="text" placeholder="ID Compra" value={ordenesEditar.idCompra} />
            <Form.Control id="cliente" type="text" placeholder="Cliente" value={ordenesEditar.cliente}/>
            <Form.Control
              id="fechaCompra"
              type="text"
              placeholder="Fecha de compra"
            />
            <Form.Control
              id="fechaEntrega"
              type="text"
              placeholder="Fecha de entrega"
            />
            <Form.Control id="courier" type="text" placeholder="Courier" />
            <Form.Control id="tracking" type="text" placeholder="Tracking" />
            <Form.Control
              id="costoCompra"
              type="text"
              placeholder="Costo de compra"
            />
            <Form.Control id="detalle" type="text" placeholder="Detalle" />
            <Form.Control
              id="observacion"
              type="text"
              placeholder="Observacion"
            />
            <Form.Control id="estado" type="text" placeholder="Estado" />
          </Stack>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={() => setIsModalEditar(false)}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={editarOrdenesModal}>
          Editar
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ModalEditar;
