import firebaseApp from "../firebase/credenciales";

import { getFirestore, collection, query, where, getDocs } from "firebase/firestore";

const db = getFirestore();

async function filtrarDatos(stringBusqueda) {
    const docsFiltrado = []

  const collectionRef = collection(db, "ordenes");
  const queryCliente = query(
    collectionRef,
    where("cliente", "==", stringBusqueda)
  );
  const spanshotCliente = await getDocs(queryCliente);

    snapshotCliente.forEach(doc => {
        docsFiltrado.push(doc);
    })

  console.log(spanshotCliente)
}

export default filtrarDatos;
