import React from "react";

import { Container, Form, Button, Stack, Table } from "react-bootstrap";

import getAllOrdenes from "../functions/getAllOrdenes";

//Modelos
import ModalAdd from "./ModalAdd";
import ModalEditar from "./ModalEditar";

function AdminView() {
  const [ordenes, setOrdenes] = React.useState([]);
  const [isModalAdd, setIsModalAdd] = React.useState(false);
  const [isModalEditar, setIsModalEditar] = React.useState(false);

  function actualizarEstadoOrdenes() {
    getAllOrdenes().then((ordenes) => {
      setOrdenes(ordenes);
    });
  }

  function addOrdenes() {
    setIsModalAdd(true);
  }

  React.useEffect(() => {
    actualizarEstadoOrdenes();
  }, []);

  return (
    <Container fluid>
      <ModalAdd
        isModalAdd={isModalAdd}
        setIsModalAdd={setIsModalAdd}
        actualizarEstadoOrdenes={actualizarEstadoOrdenes}
      />
      <ModalEditar
        isModalEditar={isModalEditar}
        setIsModalEditar={setIsModalEditar}
        actualizarEstadoOrdenes={actualizarEstadoOrdenes}
      />
      <Form>
        <Stack direction="horizontal">
          <Form.Group controlId="busqueda" className="w-75 m-3">
            <Form.Control type="text" placeholder="Buscar" />
          </Form.Group>
          <Button variant="dark" type="submit">
            Buscar
          </Button>
          <Button variant="light">Restear</Button>
        </Stack>
      </Form>

      <hr />

      <Table>
        <thead>
          <tr>
            <th>ID Compra</th>
            <th>Cliente</th>
            <th>Fecha de compra</th>
            <th>Fecha de entrega</th>
            <th>Courier</th>
            <th>Tracking</th>
            <th>Costo de compra</th>
            <th>Detalle</th>
            <th>Observacion</th>
            <th>Estado</th>
            <th>Cambiar estado</th>
          </tr>
        </thead>

        <tbody>
          {ordenes &&
            ordenes.map((orden, index) => (
              <tr key={index}>
                <td>{orden.idCompra}</td>
                <td>{orden.cliente}</td>
                <td>{orden.fechaCompra}</td>
                <td>{orden.fechaEntrega}</td>
                <td>{orden.courier}</td>
                <td>{orden.tracking}</td>
                <td>{orden.costo}</td>
                <td>{orden.detalle}</td>
                <td>{orden.observacion}</td>
                <td>{orden.estado}</td>
                <td>
                  <Button variant="dark" onClick={() => setIsModalEditar(true)}>
                    Editar estado
                  </Button>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>
      <Button onClick={addOrdenes}>Agregar ordenes</Button>
    </Container>
  );
}

export default AdminView;
